    async Task<List<string>> getRoles(string id)
    {
        UserManager<User> userManager = ViewBag.UserManager;
        var user = userManager.FindByIdAsync(id).Result;
        var roles = await userManager.GetRolesAsync(user);
        return roles.ToList();
    }

